"use client";

// ============================================================================
// STATUS CELL - Status Display
// ============================================================================
// This component is used to visually display status values with colored badges
// in tables.
//
// Features:
// - 5 predefined status colors (Active, Inactive, Pending, Completed, Cancelled)
// - Dark mode support
// - Customizable colors
// - Default gray badge for undefined statuses
// ============================================================================

/**
 * Default colors for Status Cell
 * 
 * 4 values are defined for each status:
 * - bg: Light theme background color
 * - text: Light theme text color
 * - darkBg: Dark theme background color
 * - darkText: Dark theme text color
 */
export const defaultStatusColors: Record<string, { bg: string; text: string; darkBg: string; darkText: string }> = {
    Active: {
        bg: "bg-green-100",
        text: "text-green-800",
        darkBg: "dark:bg-green-900",
        darkText: "dark:text-green-300",
    },
    Inactive: {
        bg: "bg-red-100",
        text: "text-red-800",
        darkBg: "dark:bg-red-900",
        darkText: "dark:text-red-300",
    },
    Pending: {
        bg: "bg-yellow-100",
        text: "text-yellow-800",
        darkBg: "dark:bg-yellow-900",
        darkText: "dark:text-yellow-300",
    },
    Completed: {
        bg: "bg-blue-100",
        text: "text-blue-800",
        darkBg: "dark:bg-blue-900",
        darkText: "dark:text-blue-300",
    },
    Cancelled: {
        bg: "bg-gray-100",
        text: "text-gray-800",
        darkBg: "dark:bg-gray-900",
        darkText: "dark:text-gray-300",
    },
};

export interface StatusCellProps {
    /** Status value to display (e.g., "Active", "Pending") */
    value: string;
    /** 
     * Custom color definitions
     * Used to override default colors or add new colors
     * 
     * @example
     * ```tsx
     * colors={{
     *   Custom: { bg: "bg-purple-100", text: "text-purple-800" }
     * }}
     * ```
     */
    colors?: Record<string, { bg: string; text: string; darkBg?: string; darkText?: string }>;
    /** Additional CSS classes */
    className?: string;
}

/**
 * Ready-to-use component for status cells
 * 
 * Supports the following statuses by default:
 * - **Active** → Green badge
 * - **Inactive** → Red badge  
 * - **Pending** → Yellow badge
 * - **Completed** → Blue badge
 * - **Cancelled** → Gray badge
 * 
 * Undefined statuses are automatically shown with a gray badge.
 * 
 * @example
 * // Basit kullanım
 * ```tsx
 * <StatusCell value="Active" />
 * ```
 * 
 * @example
 * // Özel renklerle kullanım
 * ```tsx
 * <StatusCell 
 *   value="OnHold" 
 *   colors={{
 *     OnHold: { 
 *       bg: "bg-orange-100", 
 *       text: "text-orange-800",
 *       darkBg: "dark:bg-orange-900",
 *       darkText: "dark:text-orange-300"
 *     }
 *   }}
 * />
 * ```
 * 
 * @example
 * // TanStack Table column içinde kullanım
 * ```tsx
 * columnHelper.accessor("status", {
 *   header: "Durum",
 *   cell: (info) => <StatusCell value={info.getValue()} />,
 * })
 * ```
 */
export function StatusCell({ value, colors, className }: StatusCellProps) {
    const mergedColors = { ...defaultStatusColors, ...colors };
    const colorConfig = mergedColors[value];

    if (!colorConfig) {
        return (
            <span className={`px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-300 ${className || ""}`}>
                {value}
            </span>
        );
    }

    const { bg, text, darkBg, darkText } = colorConfig;

    return (
        <span className={`px-2 py-1 rounded-full text-xs font-medium ${bg} ${text} ${darkBg || ""} ${darkText || ""} ${className || ""}`}>
            {value}
        </span>
    );
}

"use client";

// ============================================================================
// PROGRESS CELL - Progress Bar
// ============================================================================
// This component is used to display percentage values with a visual progress bar
// in tables.
//
// Features:
// - Customizable bar and background colors
// - Optional percentage label
// - Smooth animations
// - Value safety between 0-100
// ============================================================================

export interface ProgressCellProps {
    /** Progress value (between 0-100) */
    value: number;
    /** 
     * Progress bar color 
     * @default "bg-blue-600"
     */
    barColor?: string;
    /** 
     * Background color 
     * @default "bg-gray-200 dark:bg-gray-700"
     */
    bgColor?: string;
    /** 
     * Show percentage label 
     * @default false
     */
    showLabel?: boolean;
    /** Additional CSS classes */
    className?: string;
}

/**
 * Ready-to-use component for progress bar cells
 * 
 * Displays percentage values as a visual progress bar.
 * Value is automatically clamped between 0-100.
 * 
 * @example
 * // Basit kullanım
 * ```tsx
 * <ProgressCell value={75} />
 * ```
 * 
 * @example
 * // Etiket ile
 * ```tsx
 * <ProgressCell value={42} showLabel />
 * ```
 * 
 * @example
 * // Özel renklerle
 * ```tsx
 * <ProgressCell 
 *   value={60} 
 *   barColor="bg-green-500"
 *   bgColor="bg-green-100"
 *   showLabel
 * />
 * ```
 * 
 * @example
 * // TanStack Table column içinde kullanım
 * ```tsx
 * columnHelper.accessor("progress", {
 *   header: "İlerleme",
 *   cell: (info) => <ProgressCell value={info.getValue()} showLabel />,
 * })
 * ```
 */
export function ProgressCell({
    value,
    barColor = "bg-blue-600",
    bgColor = "bg-gray-200 dark:bg-gray-700",
    showLabel = false,
    className,
}: ProgressCellProps) {
    // Clamp value between 0-100
    const clampedValue = Math.min(100, Math.max(0, value));

    return (
        <div className={`flex items-center gap-2 ${className || ""}`}>
            <div className={`w-full rounded-full h-2.5 ${bgColor}`}>
                <div
                    className={`${barColor} h-2.5 rounded-full transition-all`}
                    style={{ width: `${clampedValue}%` }}
                />
            </div>
            {showLabel && (
                <span className="text-xs text-gray-500 dark:text-gray-400 w-8">
                    {value}%
                </span>
            )}
        </div>
    );
}

"use client";

// ============================================================================
// DATE CELL - Date Display
// ============================================================================
// This component is used to display date values in tables in different formats.
//
// Features:
// - 3 different formats: short, long, relative
// - Locale support (language and region formatting)
// - Accepts String or Date object
// - Safe fallback for invalid dates
// ============================================================================

export interface DateCellProps {
    /** Date value (ISO string or Date object) */
    value: string | Date;
    /** 
     * Date format
     * - "short": Short format (e.g., 12/28/2024)
     * - "long": Long format (e.g., December 28, 2024)
     * - "relative": Relative format (e.g., "Today", "Yesterday", "3 days ago")
     * @default "short"
     */
    format?: "short" | "long" | "relative";
    /** 
     * Locale (language and region)
     * @default "en-US"
     */
    locale?: string;
}

/**
 * Ready-to-use component for date values
 * 
 * Supported formats:
 * - **short**: Standard short date format (e.g., 12/28/2024)
 * - **long**: Long format with month name (e.g., December 28, 2024)
 * - **relative**: Relative time (Today, Yesterday, X days ago)
 * 
 * Invalid dates are automatically displayed as "-".
 * 
 * @example
 * // Simple usage (short format)
 * ```tsx
 * <DateCell value="2024-12-28" />
 * // Output: "12/28/2024"
 * ```
 * 
 * @example
 * // Long format
 * ```tsx
 * <DateCell value={new Date()} format="long" />
 * // Output: "December 28, 2024"
 * ```
 * 
 * @example
 * // Relative format
 * ```tsx
 * <DateCell value={new Date()} format="relative" />
 * // Output: "Today"
 * ```
 * 
 * @example
 * // Different locale
 * ```tsx
 * <DateCell value="2024-12-28" format="long" locale="fr-FR" />
 * // Output: "28 décembre 2024"
 * ```
 * 
 * @example
 * // Usage within TanStack Table column
 * ```tsx
 * columnHelper.accessor("createdAt", {
 *   header: "Created At",
 *   cell: (info) => <DateCell value={info.getValue()} format="long" />,
 * })
 * ```
 */
export function DateCell({ value, format = "short", locale = "en-US" }: DateCellProps) {
    const date = typeof value === "string" ? new Date(value) : value;

    // Invalid date check
    if (isNaN(date.getTime())) {
        return <span className="text-gray-400">-</span>;
    }

    let formatted: string;

    switch (format) {
        case "long":
            formatted = date.toLocaleDateString(locale, {
                year: "numeric",
                month: "long",
                day: "numeric",
            });
            break;
        case "relative": {
            const now = new Date();
            const diffMs = now.getTime() - date.getTime();
            const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

            if (diffDays === 0) formatted = "Today";
            else if (diffDays === 1) formatted = "Yesterday";
            else if (diffDays < 7) formatted = `${diffDays} days ago`;
            else formatted = date.toLocaleDateString(locale);
            break;
        }
        default:
            formatted = date.toLocaleDateString(locale);
    }

    return <span>{formatted}</span>;
}

"use client";

import * as React from "react";
import { Copy, Check } from "lucide-react";

// ============================================================================
// COPYABLE CELL - Copyable Cell
// ============================================================================
// This component is used for content that can be copied by clicking on it in tables.
// Ideal for values like Email, ID, codes that users frequently copy.
//
// Features:
// - Copy to clipboard with a single click
// - Visual feedback (✓ icon)
// - Hover effects
// - Optional callback
// ============================================================================

export interface CopyableCellProps {
    /** Value to display and copy */
    value: string | number;
    /** 
     * Duration of feedback shown after copying (ms) 
     * @default 2000
     */
    feedbackDuration?: number;
    /** Callback called after copying */
    onCopy?: (value: string) => void;
    /** Additional CSS classes */
    className?: string;
    /** 
     * Tooltip text 
     * @default "Click to copy"
     */
    tooltip?: string;
    /** 
     * Always show icon? 
     * If false, only visible on hover
     * @default false
     */
    alwaysShowIcon?: boolean;
}

/**
 * Cell component that copies value to clipboard when clicked
 * 
 * Ideal for values users frequently need to copy such as
 * Email addresses, reference codes, IDs.
 * 
 * Features:
 * - Single click copy
 * - Shows ✓ icon when copied
 * - Option to show icon on hover or always
 * - Capable of capturing copy event with optional callback
 * 
 * @example
 * // Simple usage
 * ```tsx
 * <CopyableCell value="user@example.com" />
 * ```
 * 
 * @example
 * // With Callback
 * ```tsx
 * <CopyableCell 
 *   value="ABC123" 
 *   onCopy={(val) => console.log('Copied:', val)}
 *   tooltip="Copy reference code"
 * />
 * ```
 * 
 * @example
 * // Icon always visible
 * ```tsx
 * <CopyableCell 
 *   value="order-12345" 
 *   alwaysShowIcon 
 * />
 * ```
 * 
 * @example
 * // Usage within TanStack Table column
 * ```tsx
 * columnHelper.accessor("email", {
 *   header: "Email",
 *   cell: (info) => <CopyableCell value={info.getValue()} />,
 * })
 * ```
 */
export function CopyableCell({
    value,
    feedbackDuration = 2000,
    onCopy,
    className,
    tooltip = "Click to copy",
    alwaysShowIcon = false,
}: CopyableCellProps) {
    const [copied, setCopied] = React.useState(false);
    const [isHovered, setIsHovered] = React.useState(false);

    const handleCopy = async (e: React.MouseEvent) => {
        e.stopPropagation();

        const textToCopy = String(value);

        try {
            await navigator.clipboard.writeText(textToCopy);
            setCopied(true);
            onCopy?.(textToCopy);

            setTimeout(() => {
                setCopied(false);
            }, feedbackDuration);
        } catch (err) {
            console.error("Copy error:", err);
        }
    };

    return (
        <button
            type="button"
            onClick={handleCopy}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            title={tooltip}
            className={`
                group inline-flex items-center gap-2 
                px-2 py-1 -mx-2 -my-1
                rounded-md
                transition-all duration-200
                hover:bg-slate-100 dark:hover:bg-slate-800
                focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-1
                cursor-pointer
                ${className || ""}
            `}
        >
            <span className="select-none">{value}</span>

            {/* Copy/Check icon */}
            <span
                className={`
                    inline-flex items-center justify-center
                    transition-all duration-200
                    ${alwaysShowIcon || isHovered || copied ? "opacity-100" : "opacity-0"}
                `}
            >
                {copied ? (
                    <Check className="h-3.5 w-3.5 text-green-600 dark:text-green-400" />
                ) : (
                    <Copy className="h-3.5 w-3.5 text-slate-400 dark:text-slate-500 group-hover:text-slate-600 dark:group-hover:text-slate-300" />
                )}
            </span>
        </button>
    );
}

/**
 * Factory function for CopyableCell
 * 
 * Designed for easy use with column helper.
 * Creates CopyableCell by passing the value directly.
 * 
 * @example
 * ```tsx
 * const columnHelper = createColumnHelper<User>();
 * 
 * const columns = [
 *   columnHelper.accessor("email", {
 *     header: "Email",
 *     cell: (info) => createCopyableCell(info.getValue()),
 *   }),
 *   columnHelper.accessor("orderId", {
 *     header: "Order No",
 *     cell: (info) => createCopyableCell(info.getValue(), { 
 *       tooltip: "Copy order number" 
 *     }),
 *   }),
 * ];
 * ```
 */
export function createCopyableCell(
    value: string | number,
    options?: Omit<CopyableCellProps, "value">
) {
    return <CopyableCell value={value} {...options} />;
}

"use client";

// ============================================================================
// CURRENCY CELL - Para Birimi Gösterimi
// ============================================================================
// Bu bileşen, tablolarda sayısal değerleri para birimi formatında
// göstermek için kullanılır.
//
// Özellikler:
// - Otomatik para birimi sembolü
// - Binlik ayracı ve ondalık formatı
// - Locale desteği
// - Tüm dünya para birimleri desteği (TRY, USD, EUR, GBP vb.)
// ============================================================================

export interface CurrencyCellProps {
    /** Sayısal değer */
    value: number;
    /** 
     * Para birimi kodu (ISO 4217)
     * @default "TRY"
     * @example "USD", "EUR", "GBP", "JPY"
     */
    currency?: string;
    /** 
     * Locale (dil ve bölge formatı)
     * @default "tr-TR"
     */
    locale?: string;
}

/**
 * Para birimi için hazır bileşen
 * 
 * Intl.NumberFormat kullanarak sayısal değerleri yerel formatta gösterir.
 * Para birimi sembolü, binlik ayracı ve ondalık formatı otomatik ayarlanır.
 * 
 * @example
 * // Türk Lirası (varsayılan)
 * ```tsx
 * <CurrencyCell value={1234.56} />
 * // Çıktı: "₺1.234,56"
 * ```
 * 
 * @example
 * // Amerikan Doları
 * ```tsx
 * <CurrencyCell value={1234.56} currency="USD" locale="en-US" />
 * // Çıktı: "$1,234.56"
 * ```
 * 
 * @example
 * // Euro
 * ```tsx
 * <CurrencyCell value={1234.56} currency="EUR" locale="de-DE" />
 * // Çıktı: "1.234,56 €"
 * ```
 * 
 * @example
 * // TanStack Table column içinde kullanım
 * ```tsx
 * columnHelper.accessor("price", {
 *   header: "Fiyat",
 *   cell: (info) => <CurrencyCell value={info.getValue()} />,
 * })
 * ```
 * 
 * @example
 * // Çoklu para birimi desteği
 * ```tsx
 * columnHelper.accessor("amount", {
 *   header: "Tutar",
 *   cell: (info) => (
 *     <CurrencyCell 
 *       value={info.getValue()} 
 *       currency={info.row.original.currency}
 *     />
 *   ),
 * })
 * ```
 */
export function CurrencyCell({ value, currency = "TRY", locale = "tr-TR" }: CurrencyCellProps) {
    const formatted = new Intl.NumberFormat(locale, {
        style: "currency",
        currency,
    }).format(value);

    return <span className="font-medium">{formatted}</span>;
}

"use client";

// ============================================================================
// BOOLEAN CELL - Evet/Hayır Gösterimi
// ============================================================================
// Bu bileşen, tablolarda boolean (true/false) değerlerini
// okunabilir ve renkli metin olarak göstermek için kullanılır.
//
// Özellikler:
// - Özelleştirilebilir etiketler (Yes/No, Evet/Hayır, ✓/✗ vb.)
// - Özelleştirilebilir renkler
// - Dark mode desteği
// ============================================================================

export interface BooleanCellProps {
    /** Boolean değer */
    value: boolean;
    /** 
     * True değeri için metin 
     * @default "Yes"
     */
    trueLabel?: string;
    /** 
     * False değeri için metin 
     * @default "No"
     */
    falseLabel?: string;
    /** 
     * True değeri için renk sınıfları 
     * @default "text-green-600 dark:text-green-400"
     */
    trueColor?: string;
    /** 
     * False değeri için renk sınıfları 
     * @default "text-red-600 dark:text-red-400"
     */
    falseColor?: string;
}

/**
 * Boolean değerler için hazır bileşen
 * 
 * True değerleri yeşil, false değerleri kırmızı renkte gösterir.
 * Etiketler ve renkler tamamen özelleştirilebilir.
 * 
 * @example
 * // Basit kullanım
 * ```tsx
 * <BooleanCell value={true} />
 * // Çıktı: "Yes" (yeşil)
 * ```
 * 
 * @example
 * // Türkçe etiketlerle
 * ```tsx
 * <BooleanCell value={false} trueLabel="Evet" falseLabel="Hayır" />
 * // Çıktı: "Hayır" (kırmızı)
 * ```
 * 
 * @example
 * // İkon kullanımı
 * ```tsx
 * <BooleanCell value={true} trueLabel="✓" falseLabel="✗" />
 * // Çıktı: "✓" (yeşil)
 * ```
 * 
 * @example
 * // Özel renklerle
 * ```tsx
 * <BooleanCell 
 *   value={true} 
 *   trueLabel="Onaylandı"
 *   trueColor="text-blue-600 dark:text-blue-400"
 * />
 * ```
 * 
 * @example
 * // TanStack Table column içinde kullanım
 * ```tsx
 * columnHelper.accessor("isActive", {
 *   header: "Aktif",
 *   cell: (info) => (
 *     <BooleanCell 
 *       value={info.getValue()} 
 *       trueLabel="Aktif" 
 *       falseLabel="Pasif" 
 *     />
 *   ),
 * })
 * ```
 */
export function BooleanCell({
    value,
    trueLabel = "Yes",
    falseLabel = "No",
    trueColor = "text-green-600 dark:text-green-400",
    falseColor = "text-red-600 dark:text-red-400",
}: BooleanCellProps) {
    return (
        <span className={`font-medium ${value ? trueColor : falseColor}`}>
            {value ? trueLabel : falseLabel}
        </span>
    );
}

// ============================================================================
// CELL RENDERERS - Hazır Hücre Bileşenleri
// ============================================================================
// LuxTable için önceden hazırlanmış hücre render bileşenleri.
// Bu bileşenler, tablolardaki yaygın veri tiplerini görsel olarak
// zengin bir şekilde göstermek için kullanılır.
//
// Kullanım:
// ```tsx
// import { 
//   StatusCell, 
//   ProgressCell, 
//   BooleanCell,
//   DateCell,
//   CurrencyCell,
//   CopyableCell 
// } from "@luxtable/core";
// ```
//
// Mevcut Bileşenler:
// - StatusCell     → Durum badge'leri (Active, Pending, vb.)
// - ProgressCell   → İlerleme çubuğu
// - BooleanCell    → Evet/Hayır gösterimi
// - DateCell       → Tarih formatlaması
// - CurrencyCell   → Para birimi formatlaması
// - CopyableCell   → Kopyalanabilir içerik
// ============================================================================

// Status Cell - Durum Gösterimi
export { StatusCell, defaultStatusColors } from "@/components/lux-table/cell-renderers/status-cell";
export type { StatusCellProps } from "@/components/lux-table/cell-renderers/status-cell";

// Progress Cell - İlerleme Çubuğu
export { ProgressCell } from "@/components/lux-table/cell-renderers/progress-cell";
export type { ProgressCellProps } from "@/components/lux-table/cell-renderers/progress-cell";

// Boolean Cell - Evet/Hayır Gösterimi
export { BooleanCell } from "@/components/lux-table/cell-renderers/boolean-cell";
export type { BooleanCellProps } from "@/components/lux-table/cell-renderers/boolean-cell";

// Date Cell - Tarih Gösterimi
export { DateCell } from "@/components/lux-table/cell-renderers/date-cell";
export type { DateCellProps } from "@/components/lux-table/cell-renderers/date-cell";

// Currency Cell - Para Birimi
export { CurrencyCell } from "@/components/lux-table/cell-renderers/currency-cell";
export type { CurrencyCellProps } from "@/components/lux-table/cell-renderers/currency-cell";

// Copyable Cell - Kopyalanabilir Hücre
export { CopyableCell, createCopyableCell } from "@/components/lux-table/cell-renderers/copyable-cell";
export type { CopyableCellProps } from "@/components/lux-table/cell-renderers/copyable-cell";
