"use client";

import * as React from "react";
import {
    flexRender,
    getCoreRowModel,
    useReactTable,
    getPaginationRowModel,
    getSortedRowModel,
    getFilteredRowModel,
    getFacetedUniqueValues,
    SortingState,
    ColumnFiltersState,
    RowSelectionState,
    ColumnDef,
    Row,
} from "@tanstack/react-table";
import { CheckCircle2 } from "lucide-react";
import { cn } from "@/lib/utils";
import {
    Table,
    TableHeader,
    TableBody,
    TableRow,
    TableHead,
    TableCell,
} from "@/components/table";
import { ColumnFilter } from "@/components/lux-table/column-filter";
import { TablePagination } from "@/components/lux-table/pagination";
import { TableToolbar } from "@/components/lux-table/table-toolbar";
import { Checkbox } from "@/components/ui/checkbox";
import type { LuxTableProps } from "@/components/lux-table/types";

// ============================================================================
// Selection Checkbox Column Helper
// ============================================================================

/**
 * Creates column definition for selection checkbox
 */
function createSelectionColumn<TData>(): ColumnDef<TData, unknown> {
    return {
        id: "__selection__",
        header: ({ table }) => (
            <Checkbox
                checked={
                    table.getIsAllPageRowsSelected() ||
                    (table.getIsSomePageRowsSelected() && "indeterminate")
                }
                onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
                aria-label="Select all rows"
            />
        ),
        cell: ({ row }) => (
            <Checkbox
                checked={row.getIsSelected()}
                disabled={!row.getCanSelect()}
                onCheckedChange={(value) => row.toggleSelected(!!value)}
                aria-label="Select row"
            />
        ),
        size: 40,
        enableSorting: false,
        enableHiding: false,
    };
}

// ============================================================================
// LuxTable Component
// ============================================================================

/**
 * LuxTable - Advanced React table component
 * 
 * Modern table built on top of TanStack Table, comes with ready-to-use features.
 * 
 * @example
 * ```tsx
 * // Simple usage
 * <LuxTable
 *   columns={columns}
 *   data={data}
 *   options={{
 *     pagination: true,
 *     pageSize: 20,
 *     filtering: true,
 *     sorting: true
 *   }}
 * />
 * 
 * // With row selection
 * <LuxTable
 *   columns={columns}
 *   data={data}
 *   options={{
 *     selection: "multiple", // or "single"
 *   }}
 *   onSelectedRowsChange={(selectedRows) => {
 *     console.log("Selected rows:", selectedRows);
 *   }}
 * />
 * ```
 */
export function LuxTable<TData>({
    columns,
    data,
    className,
    options,
    sorting: controlledSorting,
    onSortingChange,
    rowSelection: controlledRowSelection,
    onRowSelectionChange,
    onSelectedRowsChange,
    getRowId,
}: LuxTableProps<TData>) {
    // Internal sorting state (used when not controlled)
    const [internalSorting, setInternalSorting] = React.useState<SortingState>([]);

    // Column filters state
    const [columnFilters, setColumnFilters] = React.useState<ColumnFiltersState>([]);

    // Global filter state
    const [globalFilter, setGlobalFilter] = React.useState("");

    // Column filtering visibility (controlled by toolbar)
    const [filteringVisible, setFilteringVisible] = React.useState(options?.filtering ?? false);

    // Row selection state
    const [internalRowSelection, setInternalRowSelection] = React.useState<RowSelectionState>({});

    // Determine if we're in controlled mode
    const isControlledSorting = controlledSorting !== undefined;
    const sorting = isControlledSorting ? controlledSorting : internalSorting;

    const isControlledRowSelection = controlledRowSelection !== undefined;
    const rowSelection = isControlledRowSelection ? controlledRowSelection : internalRowSelection;

    // Selection mode configuration
    const selectionMode = options?.selection ?? "none";
    const showCheckbox = options?.showSelectionCheckbox ?? (selectionMode !== "none");
    const enableRowSelection = selectionMode !== "none";
    const enableMultiRowSelection = selectionMode === "multiple";

    // Build columns with selection column if needed
    const tableColumns = React.useMemo<ColumnDef<TData, unknown>[]>(() => {
        if (showCheckbox && enableRowSelection) {
            return [createSelectionColumn<TData>(), ...columns] as ColumnDef<TData, unknown>[];
        }
        return columns as ColumnDef<TData, unknown>[];
    }, [columns, showCheckbox, enableRowSelection]);

    // Handle row selection change
    const handleRowSelectionChange = React.useCallback(
        (updater: RowSelectionState | ((old: RowSelectionState) => RowSelectionState)) => {
            const newSelection = typeof updater === "function" ? updater(rowSelection) : updater;

            if (isControlledRowSelection && onRowSelectionChange) {
                onRowSelectionChange(newSelection);
            } else {
                setInternalRowSelection(newSelection);
            }
        },
        [isControlledRowSelection, onRowSelectionChange, rowSelection]
    );

    const table = useReactTable({
        data,
        columns: tableColumns,
        state: {
            sorting,
            columnFilters,
            rowSelection,
            globalFilter,
        },
        onGlobalFilterChange: setGlobalFilter,
        onSortingChange: (updater) => {
            const newSorting = typeof updater === "function" ? updater(sorting) : updater;

            if (isControlledSorting && onSortingChange) {
                onSortingChange(newSorting);
            } else {
                setInternalSorting(newSorting);
            }
        },
        onColumnFiltersChange: setColumnFilters,
        onRowSelectionChange: handleRowSelectionChange,
        enableRowSelection,
        enableMultiRowSelection,
        getRowId: getRowId ?? ((row: TData, index: number) => {
            // Try to use "id" field if exists, otherwise use index
            if (typeof row === "object" && row !== null && "id" in row) {
                return String((row as { id: unknown }).id);
            }
            return String(index);
        }),
        getCoreRowModel: getCoreRowModel(),
        getPaginationRowModel: options?.pagination ? getPaginationRowModel() : undefined,
        getSortedRowModel: getSortedRowModel(),
        getFilteredRowModel: getFilteredRowModel(),
        getFacetedUniqueValues: getFacetedUniqueValues(),
        initialState: {
            pagination: {
                pageSize: options?.pageSize ?? 10,
            },
        },
    });

    // Call onSelectedRowsChange when selection changes
    React.useEffect(() => {
        if (onSelectedRowsChange) {
            const selectedRows = table.getSelectedRowModel().rows.map((row: Row<TData>) => row.original);
            onSelectedRowsChange(selectedRows);
        }
    }, [rowSelection, onSelectedRowsChange, table]);

    // Calculate visible column count (for empty state colspan)
    const visibleColumnCount = tableColumns.length;

    // Toolbar visibility
    const showToolbar = options?.showToolbar ?? false;
    const showGlobalSearch = options?.showGlobalSearch ?? true;
    const showColumnVisibility = options?.showColumnVisibility ?? true;

    return (
        <div className={cn("w-full space-y-4", className)}>
            {/* Toolbar */}
            {showToolbar && (
                <TableToolbar
                    table={table}
                    showFiltering={options?.filtering !== undefined}
                    filteringEnabled={filteringVisible}
                    onFilteringToggle={setFilteringVisible}
                    showGlobalSearch={showGlobalSearch}
                    showColumnVisibility={showColumnVisibility}
                />
            )}

            {/* Selection info bar */}
            {enableRowSelection && Object.keys(rowSelection).length > 0 && (
                <div className="flex items-center gap-2 px-4 py-2 text-sm bg-blue-50 dark:bg-blue-950/50 text-blue-700 dark:text-blue-300 rounded-lg border border-blue-200 dark:border-blue-800">
                    <CheckCircle2 className="w-4 h-4" />
                    <span>
                        <strong>{Object.keys(rowSelection).length}</strong> rows selected
                        {table.getFilteredRowModel().rows.length > 0 && (
                            <span className="text-blue-500 dark:text-blue-400">
                                {" / "}{table.getFilteredRowModel().rows.length} total
                            </span>
                        )}
                    </span>
                    <button
                        type="button"
                        onClick={() => handleRowSelectionChange({})}
                        className="ml-auto text-xs hover:text-blue-900 dark:hover:text-blue-100 underline underline-offset-2"
                    >
                        Clear selection
                    </button>
                </div>
            )}

            <div className="rounded-md border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-950 overflow-hidden">
                <Table>
                    <TableHeader>
                        {table.getHeaderGroups().map((headerGroup) => (
                            <TableRow key={headerGroup.id}>
                                {headerGroup.headers.map((header) => {
                                    const isSelectionColumn = header.id === "__selection__";

                                    return (
                                        <TableHead
                                            key={header.id}
                                            style={isSelectionColumn ? { width: 40, padding: "0 12px" } : undefined}
                                        >
                                            {header.isPlaceholder
                                                ? null
                                                : (flexRender(
                                                    header.column.columnDef.header,
                                                    header.getContext()
                                                ) as React.ReactNode)}
                                        </TableHead>
                                    );
                                })}

                            </TableRow>
                        ))}

                        {/* Filter Row */}
                        {filteringVisible && (
                            <TableRow className="bg-slate-50/50 dark:bg-slate-900/50">
                                {table.getHeaderGroups()[0]?.headers.map((header) => {
                                    const isSelectionColumn = header.id === "__selection__";
                                    return (
                                        <TableHead key={`filter-${header.id}`} className="py-2">
                                            {!isSelectionColumn && header.column.getCanFilter() ? (
                                                <ColumnFilter column={header.column} />
                                            ) : null}
                                        </TableHead>
                                    );
                                })}
                            </TableRow>
                        )}
                    </TableHeader>
                    <TableBody>
                        {table.getRowModel().rows?.length ? (
                            table.getRowModel().rows.map((row) => (
                                <TableRow
                                    key={row.id}
                                    data-state={row.getIsSelected() && "selected"}
                                    className={cn(
                                        enableRowSelection && "cursor-pointer",
                                        row.getIsSelected() && "bg-blue-50/50 dark:bg-blue-950/30"
                                    )}
                                    onClick={
                                        enableRowSelection && !showCheckbox
                                            ? () => {
                                                if (selectionMode === "single") {
                                                    handleRowSelectionChange({ [row.id]: true });
                                                } else {
                                                    row.toggleSelected();
                                                }
                                            }
                                            : undefined
                                    }
                                >
                                    {row.getVisibleCells().map((cell) => {
                                        const isSelectionColumn = cell.column.id === "__selection__";
                                        return (
                                            <TableCell
                                                key={cell.id}
                                                style={isSelectionColumn ? { width: 40, padding: "0 12px" } : undefined}
                                                onClick={isSelectionColumn ? (e) => e.stopPropagation() : undefined}
                                            >
                                                {flexRender(cell.column.columnDef.cell, cell.getContext()) as React.ReactNode}
                                            </TableCell>
                                        );
                                    })}
                                </TableRow>
                            ))
                        ) : (
                            <TableRow>
                                <TableCell colSpan={visibleColumnCount} className="h-24 text-center">
                                    No results.
                                </TableCell>
                            </TableRow>
                        )}
                    </TableBody>
                </Table>
            </div>

            {/* Pagination Controls */}
            {options?.pagination && <TablePagination table={table} />}
        </div>
    );
}

"use client";

import { ColumnDef, SortingState, RowSelectionState } from "@tanstack/react-table";

// ============================================================================
// LuxTable Types
// ============================================================================

/**
 * All type definitions for LuxTable
 */

export interface LuxTableOptions {
    /** Pagination feature (default: false) */
    pagination?: boolean;
    /** Rows per page (default: 10) */
    pageSize?: number;
    /** Sorting feature (default: true) */
    sorting?: boolean;
    /** Column filtering feature (default: false) */
    filtering?: boolean;
    /** Row selection mode - "single": single select, "multiple": multi-select, "none": disabled */
    selection?: "single" | "multiple" | "none";
    /** Show selection checkbox (default: true if selection !== "none") */
    showSelectionCheckbox?: boolean;

    // Toolbar Options
    /** Show toolbar with search and controls (default: false) */
    showToolbar?: boolean;
    /** Show global search in toolbar (default: true when toolbar is shown) */
    showGlobalSearch?: boolean;
    /** Show column visibility controls in toolbar (default: true when toolbar is shown) */
    showColumnVisibility?: boolean;
}

export interface LuxTableProps<TData> {
    /** Column definitions */
    columns: ColumnDef<TData, any>[];
    /** Table data */
    data: TData[];
    /** Additional CSS classes */
    className?: string;
    /** Table options */
    options?: LuxTableOptions;
    /** Controlled sorting state */
    sorting?: SortingState;
    /** Called when sorting changes */
    onSortingChange?: (sorting: SortingState) => void;
    /** Controlled row selection state */
    rowSelection?: RowSelectionState;
    /** Called when row selection changes */
    onRowSelectionChange?: (rowSelection: RowSelectionState) => void;
    /** Called when selected rows change (with row data) */
    onSelectedRowsChange?: (rows: TData[]) => void;
    /** Unique ID field for each row (default: "id") */
    getRowId?: (row: TData, index: number) => string;
}

/**
 * Extended type for Column meta
 * Can be used in the meta field in column definitions
 */
export interface ColumnMeta {
    /** Filter type: text or select */
    filterVariant?: "text" | "select";
}

/**
 * Pagination information
 */
export interface PaginationInfo {
    pageIndex: number;
    pageSize: number;
    totalRows: number;
    totalPages: number;
}

"use client";

import * as React from "react";
import { Column } from "@tanstack/react-table";
import { Input } from "@/components/ui/input";
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from "@/components/ui/select";
import { cn } from "@/lib/utils";

// ============================================================================
// Column Filter Component
// ============================================================================

interface ColumnFilterProps<TData, TValue> {
    column: Column<TData, TValue>;
}

/**
 * Column filter component
 * Rendered as text input or select dropdown using shadcn/ui components
 * 
 * @example
 * ```tsx
 * <ColumnFilter column={column} />
 * ```
 */
export function ColumnFilter<TData, TValue>({ column }: ColumnFilterProps<TData, TValue>) {
    const columnFilterValue = column.getFilterValue();

    // Get filter type from column meta (default: text)
    const filterVariant = (column.columnDef.meta as { filterVariant?: "text" | "select" })?.filterVariant ?? "text";

    // For select filter, get unique values from the column
    const sortedUniqueValues = React.useMemo(() => {
        if (filterVariant !== "select") return [];

        const values = new Set<string>();
        column.getFacetedUniqueValues().forEach((_, key) => {
            if (key !== null && key !== undefined) {
                values.add(String(key));
            }
        });
        return Array.from(values).sort();
    }, [column, filterVariant]);

    // Select (dropdown) filter using shadcn/ui Select
    if (filterVariant === "select") {
        return (
            <Select
                value={(columnFilterValue ?? "") as string}
                onValueChange={(value) => column.setFilterValue(value === "__all__" ? undefined : value)}
            >
                <SelectTrigger
                    className={cn(
                        "h-8 text-xs",
                        "border-slate-200 dark:border-slate-700",
                        "bg-white dark:bg-slate-900",
                        "text-slate-900 dark:text-slate-100"
                    )}
                    onClick={(e) => e.stopPropagation()}
                >
                    <SelectValue placeholder="All" />
                </SelectTrigger>
                <SelectContent>
                    <SelectItem value="__all__">All</SelectItem>
                    {sortedUniqueValues.map((value) => (
                        <SelectItem key={value} value={value}>
                            {value}
                        </SelectItem>
                    ))}
                </SelectContent>
            </Select>
        );
    }

    // Text input filter using shadcn/ui Input
    return (
        <Input
            type="text"
            value={(columnFilterValue ?? "") as string}
            onChange={(e) => column.setFilterValue(e.target.value || undefined)}
            placeholder="Filter..."
            className={cn(
                "h-8 text-xs",
                "border-slate-200 dark:border-slate-700",
                "bg-white dark:bg-slate-900",
                "text-slate-900 dark:text-slate-100",
                "placeholder:text-slate-400 dark:placeholder:text-slate-500"
            )}
            onClick={(e) => e.stopPropagation()}
        />
    );
}

"use client";

import * as React from "react";
import { Table } from "@tanstack/react-table";
import {
    ChevronsLeft,
    ChevronLeft,
    ChevronRight,
    ChevronsRight,
} from "lucide-react";
import { cn } from "@/lib/utils";

// ============================================================================
// Pagination Button
// ============================================================================

interface PaginationButtonProps {
    onClick: () => void;
    disabled: boolean;
    title: string;
    children: React.ReactNode;
}

function PaginationButton({ onClick, disabled, title, children }: PaginationButtonProps) {
    return (
        <button
            className={cn(
                "inline-flex items-center justify-center rounded-md text-sm font-medium",
                "h-9 w-9",
                "border border-slate-200 dark:border-slate-800",
                "bg-white dark:bg-slate-950",
                "hover:bg-slate-100 dark:hover:bg-slate-800",
                "disabled:pointer-events-none disabled:opacity-50",
                "transition-colors"
            )}
            onClick={onClick}
            disabled={disabled}
            title={title}
        >
            {children}
        </button>
    );
}

// ============================================================================
// Page Number Button
// ============================================================================

interface PageNumberButtonProps {
    pageNum: number;
    isActive: boolean;
    onClick: () => void;
}

function PageNumberButton({ pageNum, isActive, onClick }: PageNumberButtonProps) {
    return (
        <button
            onClick={onClick}
            className={cn(
                "inline-flex items-center justify-center rounded-md text-sm font-medium",
                "h-9 w-9",
                "transition-colors",
                isActive
                    ? "bg-blue-600 text-white border border-blue-600"
                    : "border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-950 hover:bg-slate-100 dark:hover:bg-slate-800"
            )}
        >
            {pageNum + 1}
        </button>
    );
}

// ============================================================================
// Pagination Component
// ============================================================================

interface TablePaginationProps<TData> {
    table: Table<TData>;
}

/**
 * Table pagination component
 * Provides page navigation, record info and page size selection
 */
export function TablePagination<TData>({ table }: TablePaginationProps<TData>) {
    const currentPage = table.getState().pagination.pageIndex;
    const totalPages = table.getPageCount();
    const pageSize = table.getState().pagination.pageSize;
    const totalRows = table.getFilteredRowModel().rows.length;

    // Calculate visible range
    const startRow = currentPage * pageSize + 1;
    const endRow = Math.min((currentPage + 1) * pageSize, totalRows);

    // Generate page numbers to display
    const getPageNumbers = (): (number | string)[] => {
        const pages: (number | string)[] = [];

        if (totalPages <= 7) {
            // Show all pages if 7 or less
            for (let i = 0; i < totalPages; i++) {
                pages.push(i);
            }
        } else {
            // Always show first page
            pages.push(0);

            if (currentPage > 3) {
                pages.push("...");
            }

            // Show pages around current
            for (let i = Math.max(1, currentPage - 1); i <= Math.min(totalPages - 2, currentPage + 1); i++) {
                pages.push(i);
            }

            if (currentPage < totalPages - 4) {
                pages.push("...");
            }

            // Always show last page
            pages.push(totalPages - 1);
        }

        return pages;
    };

    return (
        <div className="flex flex-col sm:flex-row items-center justify-between gap-4 px-2 py-3">
            {/* Left side: Records info + Page size selector */}
            <div className="flex items-center gap-4">
                {/* Records info */}
                <div className="text-sm text-slate-500 dark:text-slate-400">
                    <span className="font-medium text-slate-700 dark:text-slate-300">
                        {startRow}
                    </span>
                    -
                    <span className="font-medium text-slate-700 dark:text-slate-300">
                        {endRow}
                    </span>{" "}
                    of{" "}
                    <span className="font-medium text-slate-700 dark:text-slate-300">
                        {totalRows}
                    </span>{" "}
                    records shown
                </div>

                {/* Page size selector */}
                <div className="flex items-center gap-2">
                    <span className="text-sm text-slate-500 dark:text-slate-400">Rows per page:</span>
                    <select
                        value={pageSize}
                        onChange={(e) => {
                            table.setPageSize(Number(e.target.value));
                        }}
                        className={cn(
                            "h-9 rounded-md border border-slate-200 dark:border-slate-800",
                            "bg-white dark:bg-slate-950",
                            "px-3 py-1 text-sm",
                            "focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-1",
                            "cursor-pointer"
                        )}
                    >
                        {[10, 20, 30, 50, 100].map((size) => (
                            <option key={size} value={size}>
                                {size}
                            </option>
                        ))}
                    </select>
                </div>
            </div>

            {/* Right side: Navigation */}
            <div className="flex items-center gap-2">
                {/* First page */}
                <PaginationButton
                    onClick={() => table.setPageIndex(0)}
                    disabled={!table.getCanPreviousPage()}
                    title="First page"
                >
                    <ChevronsLeft className="h-4 w-4" />
                </PaginationButton>

                {/* Previous page */}
                <PaginationButton
                    onClick={() => table.previousPage()}
                    disabled={!table.getCanPreviousPage()}
                    title="Previous page"
                >
                    <ChevronLeft className="h-4 w-4" />
                </PaginationButton>

                {/* Page numbers */}
                <div className="flex items-center gap-1">
                    {getPageNumbers().map((page, idx) => {
                        if (page === "...") {
                            return (
                                <span key={`ellipsis-${idx}`} className="px-2 text-slate-400">
                                    ...
                                </span>
                            );
                        }

                        const pageNum = page as number;
                        return (
                            <PageNumberButton
                                key={pageNum}
                                pageNum={pageNum}
                                isActive={pageNum === currentPage}
                                onClick={() => table.setPageIndex(pageNum)}
                            />
                        );
                    })}
                </div>

                {/* Next page */}
                <PaginationButton
                    onClick={() => table.nextPage()}
                    disabled={!table.getCanNextPage()}
                    title="Next page"
                >
                    <ChevronRight className="h-4 w-4" />
                </PaginationButton>

                {/* Last page */}
                <PaginationButton
                    onClick={() => table.setPageIndex(table.getPageCount() - 1)}
                    disabled={!table.getCanNextPage()}
                    title="Last page"
                >
                    <ChevronsRight className="h-4 w-4" />
                </PaginationButton>
            </div>
        </div>
    );
}

"use client";

import * as React from "react";
import { Table } from "@tanstack/react-table";
import {
    Search,
    Filter,
    FilterX,
    Columns3,
    Eye,
    EyeOff,
    RotateCcw,
    ChevronDown,
} from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuLabel,
    DropdownMenuSeparator,
    DropdownMenuTrigger,
    DropdownMenuCheckboxItem,
} from "@/components/ui/dropdown-menu";

// ============================================================================
// TableToolbar Component
// ============================================================================

interface TableToolbarProps<TData> {
    /** TanStack Table instance */
    table: Table<TData>;
    /** Whether column filtering is enabled */
    showFiltering?: boolean;
    /** Callback to toggle column filtering UI */
    onFilteringToggle?: (enabled: boolean) => void;
    /** Current filtering state */
    filteringEnabled?: boolean;
    /** Show global search input */
    showGlobalSearch?: boolean;
    /** Show column visibility controls */
    showColumnVisibility?: boolean;
    /** Additional CSS classes */
    className?: string;
}

/**
 * TableToolbar - A toolbar component for LuxTable
 * 
 * Provides controls for:
 * - Global search across all columns
 * - Column visibility toggle (show/hide columns)
 * - Column filtering toggle (on/off)
 */
export function TableToolbar<TData>({
    table,
    showFiltering = true,
    onFilteringToggle,
    filteringEnabled = false,
    showGlobalSearch = true,
    showColumnVisibility = true,
    className,
}: TableToolbarProps<TData>) {
    const [globalFilter, setGlobalFilter] = React.useState("");

    // Get all columns that can be hidden
    const hidableColumns = table
        .getAllColumns()
        .filter((column) => column.getCanHide() && column.id !== "__selection__");

    // Get hidden columns
    const hiddenColumns = hidableColumns.filter((column) => !column.getIsVisible());

    // Handle global search
    const handleGlobalSearch = React.useCallback(
        (value: string) => {
            setGlobalFilter(value);
            table.setGlobalFilter(value);
        },
        [table]
    );

    // Reset all column visibility
    const handleResetVisibility = React.useCallback(() => {
        table.resetColumnVisibility();
    }, [table]);

    // Show all hidden columns
    const handleShowAllColumns = React.useCallback(() => {
        hidableColumns.forEach((column) => {
            column.toggleVisibility(true);
        });
    }, [hidableColumns]);

    return (
        <div
            className={cn(
                "flex flex-wrap items-center gap-2 p-3 rounded-lg border border-slate-200 dark:border-slate-800 bg-slate-50/50 dark:bg-slate-900/50",
                className
            )}
        >
            {/* Global Search */}
            {showGlobalSearch && (
                <div className="relative flex-1 min-w-[200px] max-w-sm">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-slate-400" />
                    <input
                        type="text"
                        placeholder="Search in all columns..."
                        value={globalFilter}
                        onChange={(e) => handleGlobalSearch(e.target.value)}
                        className={cn(
                            "w-full h-9 pl-9 pr-3 rounded-md border border-slate-200 dark:border-slate-700",
                            "bg-white dark:bg-slate-950 text-sm text-slate-900 dark:text-slate-100",
                            "placeholder:text-slate-400 dark:placeholder:text-slate-500",
                            "focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500",
                            "transition-colors"
                        )}
                    />
                    {globalFilter && (
                        <button
                            onClick={() => handleGlobalSearch("")}
                            className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded-full hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                        >
                            <FilterX className="h-3 w-3 text-slate-400" />
                        </button>
                    )}
                </div>
            )}

            <div className="flex items-center gap-2 ml-auto">
                {/* Toggle Column Filtering */}
                {showFiltering && onFilteringToggle && (
                    <Button
                        variant={filteringEnabled ? "default" : "outline"}
                        size="sm"
                        onClick={() => onFilteringToggle(!filteringEnabled)}
                        className="gap-2"
                    >
                        {filteringEnabled ? (
                            <>
                                <FilterX className="h-4 w-4" />
                                <span className="hidden sm:inline">Hide Filters</span>
                            </>
                        ) : (
                            <>
                                <Filter className="h-4 w-4" />
                                <span className="hidden sm:inline">Show Filters</span>
                            </>
                        )}
                    </Button>
                )}

                {/* Column Visibility Dropdown */}
                {showColumnVisibility && (
                    <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                            <Button variant="outline" size="sm" className="gap-2">
                                <Columns3 className="h-4 w-4" />
                                <span className="hidden sm:inline">Columns</span>
                                {hiddenColumns.length > 0 && (
                                    <span className="ml-1 flex h-5 w-5 items-center justify-center rounded-full bg-blue-500 text-[10px] font-medium text-white">
                                        {hiddenColumns.length}
                                    </span>
                                )}
                                <ChevronDown className="h-3 w-3 opacity-50" />
                            </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end" className="w-56">
                            <DropdownMenuLabel className="flex items-center gap-2">
                                <Columns3 className="h-4 w-4" />
                                Toggle Columns
                            </DropdownMenuLabel>
                            <DropdownMenuSeparator />

                            {/* Quick actions */}
                            <div className="flex items-center gap-1 px-2 py-1.5">
                                <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={handleShowAllColumns}
                                    disabled={hiddenColumns.length === 0}
                                    className="h-7 flex-1 text-xs"
                                >
                                    <Eye className="mr-1 h-3 w-3" />
                                    Show All
                                </Button>
                                <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={handleResetVisibility}
                                    className="h-7 flex-1 text-xs"
                                >
                                    <RotateCcw className="mr-1 h-3 w-3" />
                                    Reset
                                </Button>
                            </div>

                            <DropdownMenuSeparator />

                            {/* Column list */}
                            <div className="max-h-[300px] overflow-y-auto">
                                {hidableColumns.map((column) => {
                                    const columnDef = column.columnDef;
                                    const headerText =
                                        typeof columnDef.header === "string"
                                            ? columnDef.header
                                            : column.id;

                                    return (
                                        <DropdownMenuCheckboxItem
                                            key={column.id}
                                            checked={column.getIsVisible()}
                                            onCheckedChange={(value) => column.toggleVisibility(!!value)}
                                            className="capitalize"
                                        >
                                            <span className="flex items-center gap-2">
                                                {column.getIsVisible() ? (
                                                    <Eye className="h-3.5 w-3.5 text-green-500" />
                                                ) : (
                                                    <EyeOff className="h-3.5 w-3.5 text-slate-400" />
                                                )}
                                                {headerText}
                                            </span>
                                        </DropdownMenuCheckboxItem>
                                    );
                                })}
                            </div>

                            {hiddenColumns.length > 0 && (
                                <>
                                    <DropdownMenuSeparator />
                                    <div className="px-2 py-1.5 text-xs text-slate-500 dark:text-slate-400">
                                        {hiddenColumns.length} column(s) hidden
                                    </div>
                                </>
                            )}
                        </DropdownMenuContent>
                    </DropdownMenu>
                )}
            </div>
        </div>
    );
}

